sequence.script:

var id = mc.getProperty('uri.var.id');
var patient = mc.getProperty('uri.var.patient');
var startDate = mc.getProperty('uri.var.startDate');
var class = mc.getProperty('uri.var.class');
var endDate = mc.getProperty('uri.var.endDate');
urlQuery = '';
if  (id != null && id != "") {
    urlQuery += '_id=' + id + '&';
}
if  (patient != null && patient != "") {
    urlQuery += 'patient=' + patient + '&';
}
if  (class != null && class != "") {
    urlQuery += 'class=' + class + '&';
}
if  (startDate != null && startDate != "") {
    urlQuery += 'created=gt' + startDate + '&';
}
if  (endDate != null && endDate != "") {
    urlQuery += 'created=lt' + endDate + '&';
}
if  (urlQuery != null && urlQuery  != "") {
    urlQuery = '?' + urlQuery.toString().substring(0, urlQuery.toString().lastIndexOf("&"));
}
mc.setProperty('uri.var.urlQuery',urlQuery);
